mapper.select:
select t1.id as stockId, t1.goods_sn as goodsSn, t1.s_goods_sn as sGoodsSn, t2.id as goodsId, t2.goods_name as goodsName, t2.preview_img as previewImg, t2.supply_id as supplyId, t1.spec_id_list as specIdList, t1.cost_price as costPrice, t1.sell_price as sellPrice, t1.weight, t1.sale_flag as saleFlag, t1.warn_ratio as warnRatio, t1.stock_num as stockNum, t1.warn_stock_num as warnStockNum, t1.warn_stock as warnStock, t1.delete_flag as deleteFlag, DATE_FORMAT(t1.update_time,'%Y-%m-%d %H:%i:%s') as updateTime, DATE_FORMAT(t1.create_time,'%Y-%m-%d %H:%i:%s') as createTime, t1.sort_flag as sortFlag, t1.properties from s_goods_stock t1 left join s_goods t2 on t1.goods_sn = t2.goods_sn
1=1
and t1.id = #{id}

select t1.id as stockId, t1.goods_sn as goodsSn, t1.s_goods_sn as sGoodsSn, t2.id as goodsId, t2.supply_id as supplyId, t2.goods_name as goodsName, t2.preview_img as previewImg, t1.spec_id_list as specIdList, t1.cost_price as costPrice, t1.sell_price as sellPrice, t1.weight, t1.warn_ratio as warnRatio, t1.stock_num as stockNum, t1.warn_stock_num as warnStockNum, t1.warn_stock as warnStock, t1.delete_flag as deleteFlag, DATE_FORMAT(t1.update_time,'%Y-%m-%d %H:%i:%s') as updateTime, DATE_FORMAT(t1.create_time,'%Y-%m-%d %H:%i:%s') as createTime, t1.sort_flag as sortFlag, t1.properties from s_goods_stock t1 left join s_goods t2 on t1.goods_sn = t2.goods_sn
1=1
and t1.s_goods_sn = #{sid}

mapper.update:
update
s_goods_stock SET

    stock_num = stock_num+#{nums}


    stock_num = stock_num-#{nums}

where s_goods_sn = #{sGoodsSn}
